//draws splash screen
void draw_splash(){
	gdi_sprite_t title_spr;
	gdi_sprite_t enemy1_spr;
	gdi_sprite_t enemy2_spr;
	gdi_sprite_t enemy3_spr;
	gdi_sprite_t enemy4_spr;

	title_spr.x = (SCREEN_WIDTH - TITLE_WIDTH) / 2;
	title_spr.y =  20;
	title_spr.width  = TITLE_WIDTH;
	title_spr.height = TITLE_HEIGHT;
	title_spr.data   = title_data;

	goto_xy(0 ,0);

	set_text_justify(0,0);

	//draws title screen
	set_color_rgb(0,255,0);
	draw_sprite2(&title_spr);

	//draws Enemy3
	enemy3_spr.x = (SCREEN_WIDTH - ENEMY3_WIDTH) / 2 - LEFT_SHIFT;
	enemy3_spr.y = title_spr.y + TITLE_HEIGHT + 10;
	enemy3_spr.width = ENEMY3_WIDTH;
	enemy3_spr.height = ENEMY3_HEIGHT;
	enemy3_spr.data = (uint8_t*)enemy3_ap2;

	//draws Enemy2
	enemy2_spr.x = (SCREEN_WIDTH - ENEMY2_WIDTH) / 2 - LEFT_SHIFT;
	enemy2_spr.y = enemy3_spr.y + ENEMY3_HEIGHT + 10;
	enemy2_spr.width = ENEMY2_WIDTH;
	enemy2_spr.height = ENEMY2_HEIGHT;
	enemy2_spr.data = (uint8_t*)enemy2_ap2;


	//draws Enemy1
	enemy1_spr.x = (SCREEN_WIDTH - ENEMY1_WIDTH) / 2 - LEFT_SHIFT;
	enemy1_spr.y = enemy2_spr.y + ENEMY2_HEIGHT + 10;
	enemy1_spr.width = ENEMY1_WIDTH;
	enemy1_spr.height = ENEMY1_HEIGHT;
	enemy1_spr.data = (uint8_t*)enemy1_ap2;

	//draws mothership
	enemy4_spr.x = (SCREEN_WIDTH - ENEMY4_WIDTH) / 2 - LEFT_SHIFT;
	enemy4_spr.y = enemy1_spr.y + ENEMY1_HEIGHT + 10;
	enemy4_spr.width = ENEMY4_WIDTH;
	enemy4_spr.height = ENEMY4_HEIGHT;
	enemy4_spr.data = (uint8_t*)enemy4_ap1;


	select_font(ARIAL_BOLD_14);

	set_color(WHITE);
	draw_sprite2(&enemy3_spr);
	goto_xy(enemy3_spr.x+ENEMY3_WIDTH+5,enemy3_spr.y);
	draw_string("= 10 PTS");

	draw_sprite2(&enemy2_spr);
	goto_xy(enemy2_spr.x+ENEMY2_WIDTH+5,enemy2_spr.y);
	draw_string("= 20 PTS");

	draw_sprite2(&enemy1_spr);
	goto_xy(enemy1_spr.x+ENEMY1_WIDTH+5,enemy1_spr.y);
	draw_string("= 40 PTS");




	goto_xy(SCREEN_WIDTH/2, SCREEN_HEIGHT-40);
	set_text_justify(1,0);
	draw_string("(c) Braendle, Giacomuzzi, Jung, Mayr, Speckle, Zerlauth");




}
